select * from faq;



-- 미용/병원 예약 (계속)
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '예약 가능한 미용 스타일에는 어떤 것들이 있나요?', '펫케어를 통해 예약 가능한 미용 스타일은 각 미용실마다 제공하는 서비스에 따라 다릅니다. 일반적으로 기본 목욕, 부분 미용(발, 얼굴, 위생), 전체 미용(클리핑, 가위컷), 스파, 특정 견종/묘종 전문 스타일링 등이 있습니다. 미용실 상세 정보 페이지에서 제공하는 미용 옵션과 가격을 확인하시고, 예약 시 원하시는 스타일을 구체적으로 요청사항에 기재해주시면 더욱 만족스러운 서비스를 받으실 수 있습니다. 일부 미용실은 포트폴리오 사진을 제공하니 참고하세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '예약 변경 또는 취소는 어떻게 하나요?', '예약 변경이나 취소는 ''마이페이지'' > ''예약 내역 확인''에서 가능합니다. 해당 예약 건을 선택하신 후 ''예약 변경'' 또는 ''예약 취소'' 버튼을 클릭하여 진행해 주세요. 다만, 각 미용실이나 병원의 정책에 따라 예약 변경/취소 가능 시점이나 위약금 규정이 다를 수 있습니다. 보통 예약일 하루 전 또는 몇 시간 전까지는 수수료 없이 변경/취소가 가능하지만, 임박한 시점에는 위약금이 발생할 수 있으니 예약 시 안내되는 취소/환불 규정을 반드시 확인해 주시기 바랍니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '예약 시간보다 늦으면 어떻게 되나요?', '예약 시간에 늦으실 경우, 다음 예약 고객에게 영향이 갈 수 있어 서비스 제공이 어렵거나 대기 시간이 길어질 수 있습니다. 예상보다 늦을 것 같다면 즉시 해당 미용실이나 병원에 직접 연락하여 상황을 알리고 조율하시는 것이 좋습니다. 펫케어 앱 내 예약 상세 정보에서 업체 연락처를 확인하실 수 있습니다. 노쇼(No-Show)의 경우, 정책에 따라 위약금이 발생하거나 향후 서비스 이용에 제한이 있을 수 있으니 주의해 주십시오.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '온라인 예약이 불가능한 병원/미용실은 어떻게 이용하나요?', '펫케어에 등록된 모든 병원이나 미용실이 온라인 예약 기능을 제공하는 것은 아닙니다. 일부 업체의 경우 전화 예약만 가능할 수 있습니다. 업체 상세 정보 페이지에 온라인 예약 버튼이 없는 경우, 제공된 전화번호로 직접 연락하여 예약 문의를 해주셔야 합니다. 저희는 더 많은 업체가 온라인 예약을 지원할 수 있도록 지속적으로 노력하고 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '미용 전 준비해야 할 사항이 있나요?', '미용 전에는 반려동물의 컨디션이 좋은지 확인해 주시고, 특별히 주의해야 할 건강 문제(피부 질환, 알러지, 특정 부위 민감성 등)가 있다면 미용사에게 미리 알려주시는 것이 중요합니다. 또한, 원활한 미용 진행을 위해 방문 전 가벼운 산책으로 배변 활동을 유도해 주시면 좋습니다. 평소 사용하는 빗이나 특정 제품이 있다면 가져가셔도 되지만, 대부분의 미용실에는 전문 용품이 구비되어 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '동물병원 진료 과목은 어떤 것들이 있나요?', '펫케어를 통해 검색 가능한 동물병원들은 내과, 외과, 안과, 치과, 피부과, 산과 등 다양한 진료 과목을 다루고 있습니다. 일부 병원은 특정 질환 전문 센터(심장, 신장 등)를 운영하기도 합니다. 병원 상세 정보 페이지에서 주요 진료 과목과 보유 장비 등을 확인하실 수 있으며, 방문 전 전화로 반려동물의 증상을 설명하고 해당 진료가 가능한지 문의하시는 것이 정확합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '야간이나 공휴일에 진료하는 병원도 찾을 수 있나요?', '네, 펫케어 검색 필터 기능을 이용하시면 야간 진료나 공휴일 진료가 가능한 동물병원을 찾아보실 수 있습니다. ''병원 서비스'' 메뉴에서 지역 선택 후, 검색 옵션에서 ''야간 진료'' 또는 ''공휴일 진료''를 체크하여 검색해 보세요. 다만, 응급 상황 발생 시에는 가장 가까운 응급 동물병원으로 즉시 연락하시거나 내원하시는 것이 중요합니다.', 'admin');

-- 마이페이지 기능 (추가)
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 정보는 여러 마리 등록할 수 있나요?', '네, ''마이페이지'' > ''반려동물 관리'' 메뉴에서 여러 마리의 반려동물 정보를 각각 등록하고 관리하실 수 있습니다. 각 반려동물별로 이름, 종류, 품종, 생일, 성별, 중성화 여부, 예방접종 내역 등을 상세히 기록할 수 있으며, 이 정보는 맞춤형 서비스 추천이나 다이어리 작성 시 활용됩니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 다이어리에 사진 용량 제한이 있나요?', '반려동물 다이어리에 사진 업로드 시, 파일당 용량 제한이 있을 수 있습니다. 일반적으로 고화질의 사진도 충분히 업로드 가능하도록 설정되어 있지만, 지나치게 큰 파일(예: 수십MB 이상)은 업로드가 느리거나 실패할 수 있습니다. 최적의 환경을 위해 적절한 크기로 사진을 조절하여 업로드해 주시면 감사하겠습니다. 현재 파일당 최대 10MB까지 지원하고 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '내가 쓴 글이나 댓글은 어디서 모아볼 수 있나요?', '로그인 후 ''마이페이지''로 이동하시면 ''내가 쓴 글'', ''내가 쓴 댓글'' 메뉴를 통해 커뮤니티, Q&A 등 펫케어 서비스 내에서 작성하신 모든 게시글과 댓글을 한눈에 확인하고 관리하실 수 있습니다. 해당 페이지에서 글 수정이나 삭제도 가능합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '알림 설정은 어떻게 변경하나요?', '''마이페이지'' > ''알림 설정'' 메뉴에서 서비스 이용과 관련된 다양한 알림(예: 예약 확인, 댓글 알림, 이벤트 정보 등)의 수신 여부를 개별적으로 설정하거나 전체적으로 변경할 수 있습니다. 푸시 알림, 이메일 알림, SMS 알림 등 수신 채널별 설정도 가능하니, 원하시는 방식으로 편리하게 정보를 받아보세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '찜한 미용실/병원/상품 목록은 어디서 보나요?', '서비스를 이용하시면서 마음에 드는 미용실, 동물병원, 또는 반려동물 용품을 발견했을 때 ''찜하기'' 기능을 사용하셨다면, ''마이페이지'' 내의 ''찜 목록'' 또는 각 서비스(미용, 병원, 상품정보) 내 ''내가 찜한 곳''과 같은 메뉴에서 모아보실 수 있습니다. 이를 통해 나중에 다시 찾아보거나 예약/구매 시 편리하게 이용 가능합니다.', 'admin');

-- 정보 서비스 (추가)
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '상품 정보의 가격은 실시간으로 반영되나요?', '펫케어에서 제공하는 상품 정보는 제휴 쇼핑몰 또는 데이터 크롤링을 통해 수집되며, 가격 정보는 최대한 실시간에 가깝게 업데이트하려고 노력하고 있습니다. 하지만, 각 판매처의 정책이나 프로모션에 따라 가격이 수시로 변동될 수 있으므로, 실제 구매 시점에는 해당 판매 사이트에서 최종 가격을 반드시 재확인해 주시기 바랍니다. 펫케어는 정보 중개 플랫폼으로, 가격 변동에 따른 책임을 지지 않습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '특정 질환에 좋은 사료 정보를 얻고 싶어요.', '''반려동물 정보'' > ''맞춤 사료 찾기'' 또는 ''전문가 Q&A'' 게시판을 이용하시면 도움을 받으실 수 있습니다. 맞춤 사료 찾기 기능에서는 반려동물의 연령, 크기, 활동량, 특정 건강 고민(예: 피부, 관절, 비만 등)에 따라 추천 사료 정보를 제공하려고 노력 중입니다. 더 전문적인 조언이 필요하시면 수의사와의 상담을 권장하며, Q&A 게시판에 질문을 남겨주시면 전문가나 경험 많은 반려인들의 답변을 얻을 수도 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 관련 법규나 정책 정보도 알 수 있나요?', '펫케어는 반려동물 양육에 필요한 다양한 정보를 제공하기 위해 노력하고 있으며, ''반려동물 정보'' 또는 ''커뮤니티'' 내 공지사항 등을 통해 동물보호법 개정 내용, 펫티켓 관련 규정, 지자체 지원 정책 등 주요 법규 및 정책 정보를 요약하여 안내해 드리고자 합니다. 다만, 가장 정확하고 최신의 정보는 정부 공식 부처(예: 농림축산식품부, 지자체 동물보호과)의 발표 자료를 직접 확인하시는 것이 좋습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '전문가 Q&A 답변은 얼마나 걸리나요?', '전문가 Q&A 게시판에 질문을 남기시면, 제휴된 수의사, 훈련사 등 전문가 또는 저희 운영팀이 확인 후 답변을 드리고 있습니다. 답변까지 소요되는 시간은 질문의 난이도나 전문가의 상황에 따라 다소 차이가 있을 수 있으며, 보통 영업일 기준 1~3일 이내에 답변을 드리려고 노력하고 있습니다. 긴급한 상담이 필요한 경우에는 가까운 동물병원에 직접 문의하시는 것이 가장 빠릅니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '찾는 품종 정보가 없어요. 추가 요청할 수 있나요?', '펫케어는 다양한 견종 및 묘종 정보를 제공하기 위해 지속적으로 데이터베이스를 확장하고 있습니다. 만약 찾으시는 특정 품종 정보가 없을 경우, ''고객센터'' > ''1:1 문의하기''를 통해 품종 정보 추가를 요청해 주시면 내부 검토 후 최대한 반영하도록 하겠습니다. 사용자분들의 의견을 소중히 듣고 서비스를 개선해나가겠습니다.', 'admin');

-- 커뮤니티 (추가)
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '커뮤니티 게시판 이용 규칙은 무엇인가요?', '펫케어 커뮤니티는 모든 사용자가 즐겁고 유익하게 정보를 나눌 수 있는 공간을 지향합니다. 이를 위해 비방, 욕설, 광고성 스팸, 음란물, 개인정보 침해, 저작권 위반 등 다른 사용자에게 불쾌감을 주거나 피해를 줄 수 있는 게시물 및 댓글 작성을 금지하고 있습니다. 자세한 이용 규칙은 커뮤니티 공지사항 또는 서비스 이용 약관을 참고해 주시기 바랍니다. 규칙 위반 시에는 게시물 삭제 및 계정 이용 제한 조치가 취해질 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '게시글에 동영상도 첨부할 수 있나요?', '현재 펫케어 커뮤니티 게시글 작성 시, 이미지 파일 첨부는 가능하지만 동영상 파일 직접 첨부 기능은 지원하고 있지 않습니다. 동영상을 공유하고 싶으시다면, 유튜브(YouTube)나 비메오(Vimeo) 등 외부 동영상 플랫폼에 업로드하신 후 해당 영상의 링크를 게시글 본문에 포함하여 공유해 주시면 됩니다. 향후 동영상 직접 업로드 기능 지원도 검토하도록 하겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '커뮤니티에서 특정 사용자를 차단하고 싶어요.', '다른 사용자로부터 원치 않는 메시지나 게시글을 계속 보게 되어 불편하시다면, 해당 사용자를 차단하는 기능을 이용할 수 있습니다. 사용자 프로필 페이지나 게시글에서 ''사용자 차단하기'' 옵션을 선택하시면, 차단된 사용자의 게시글이나 댓글이 회원님께 더 이상 보이지 않게 됩니다. 차단 목록은 ''마이페이지'' > ''사용자 관리''에서 확인 및 해제할 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '커뮤니티에서 스터디 그룹이나 소모임을 만들 수 있나요?', '현재 펫케어 커뮤니티는 자유게시판, 정보공유 게시판 등 주제별 게시판을 중심으로 운영되고 있으며, 사용자가 직접 스터디 그룹이나 소모임을 개설하고 관리하는 기능은 공식적으로 제공하고 있지 않습니다. 하지만, 특정 주제에 관심 있는 사용자들끼리 게시판을 통해 의견을 나누고 자율적으로 모임을 결성하여 활동하시는 것은 가능합니다. 향후 사용자 편의를 위한 그룹 기능 도입도 고려해보겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '신고된 게시글은 어떻게 처리되나요?', '사용자로부터 신고가 접수된 게시글이나 댓글은 펫케어 운영팀에서 신고 사유와 해당 콘텐츠 내용을 면밀히 검토합니다. 커뮤니티 이용 규칙 위반이 확인될 경우, 해당 게시물은 경고 없이 삭제될 수 있으며, 사안의 경중에 따라 작성자에게는 서비스 이용 제한 등의 조치가 취해질 수 있습니다. 신고 처리 결과는 신고자에게 개별적으로 통보되지 않을 수 있으나, 신고 시스템이 원활히 운영되도록 최선을 다하고 있습니다.', 'admin');

-- 입양/분양 (추가)
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '입양/분양 게시글 작성 시 어떤 정보를 필수로 입력해야 하나요?', '책임감 있는 입양 및 분양 문화를 위해, 게시글 작성 시 반려동물의 종류, 품종, 나이, 성별, 건강 상태(예방접종, 중성화 여부, 특이사항 등), 성격, 그리고 입양/분양 조건(책임비 유무, 조건 등)을 최대한 상세하고 정확하게 기재해 주셔야 합니다. 또한, 반려동물의 현재 모습을 잘 보여주는 사진을 여러 장 첨부하시는 것이 좋습니다. 허위 정보를 기재할 경우 불이익을 받을 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '입양/분양 시 책임비는 꼭 받아야 하나요? 얼마가 적당한가요?', '책임비는 새로운 가족을 찾는 반려동물이 무분별하게 거래되거나 유기되는 것을 방지하고, 최소한의 책임감을 확인하기 위한 목적으로 주고받는 금액입니다. 필수는 아니지만 권장되는 사항입니다. 책임비의 액수는 정해진 기준은 없으나, 보통 기본 검진 비용, 예방 접종 비용 등을 고려하여 책정되며, 상업적 목적의 과도한 금액은 지양해야 합니다. 책임비는 입양 후 해당 동물을 위해 사용되는 것이 바람직합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '입양 전에 해당 동물을 직접 만나볼 수 있나요?', '네, 입양은 평생을 함께할 가족을 맞이하는 중요한 결정이므로, 가능하면 입양 전에 해당 동물을 직접 만나보고 교감하는 시간을 갖는 것이 좋습니다. 게시글 작성자와 충분히 소통하여 만남 일정과 장소를 조율해 보세요. 만남 시에는 동물의 건강 상태, 성격 등을 꼼꼼히 확인하고, 궁금한 점들을 충분히 질문하시는 것이 중요합니다. 안전을 위해 공공장소에서의 만남을 권장합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '유기동물 보호소 정보도 펫케어에서 볼 수 있나요?', '펫케어는 ''입양/분양'' 게시판을 통해 개인 간의 입양/분양 정보뿐만 아니라, 일부 제휴된 유기동물 보호소의 입양 대기 동물 정보를 제공하기 위해 노력하고 있습니다. 또한, 공공데이터포털 등과 연계하여 전국 유기동물 정보를 조회할 수 있는 링크나 기능을 제공하는 방안도 검토 중입니다. 사설 보호소나 개인 구조자의 동물들도 입양 게시판을 통해 소개될 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '입양/분양 과정에서 사기를 당하지 않으려면 어떻게 해야 하나요?', '안전한 입양/분양을 위해 몇 가지 주의사항이 필요합니다. 첫째, 지나치게 좋은 조건(무료 분양, 고가의 품종견 등)을 제시하거나 연락처 등 기본 정보를 명확히 밝히지 않는 경우 주의해야 합니다. 둘째, 직접 만나서 동물을 확인하기 전에 선입금을 요구하는 경우 응하지 않는 것이 좋습니다. 셋째, 계약서(입양계약서)를 작성하여 분양 조건, 동물 정보, 양도인/양수인 정보를 명시하는 것이 안전합니다. 의심스러운 경우 거래를 중단하고 고객센터에 신고해 주세요.', 'admin');

-- 고객센터 (추가)
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '1:1 문의 답변은 보통 얼마나 걸리나요?', '고객센터 1:1 문의를 통해 접수된 내용은 담당자가 순차적으로 확인하여 답변을 드리고 있습니다. 일반적인 문의의 경우 영업일 기준 24~48시간 이내에 답변을 드리기 위해 노력하고 있으나, 문의 내용의 복잡성이나 내부 확인 절차가 필요한 경우 다소 시간이 더 소요될 수 있습니다. 최대한 신속하고 정확한 답변을 드릴 수 있도록 최선을 다하겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '전화 상담도 가능한가요?', '현재 펫케어 고객센터는 효율적인 상담 기록 관리 및 정확한 정보 전달을 위해 온라인 1:1 문의를 중심으로 운영되고 있습니다. 전화 상담은 제한적으로 운영되거나 특정 사안에 대해서만 지원될 수 있습니다. 1:1 문의를 남겨주시면 내용 확인 후 필요시 전화 연락을 드릴 수도 있습니다. 긴급한 사안이 아니라면 가급적 1:1 문의를 이용해 주시면 감사하겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '서비스 개선 아이디어를 제안하고 싶어요.', '펫케어 서비스 발전을 위한 회원님들의 소중한 아이디어나 건의사항은 언제든지 환영합니다. ''고객센터'' > ''1:1 문의하기'' 메뉴에서 문의 유형을 ''건의사항'' 또는 ''아이디어 제안'' 등으로 선택하여 내용을 작성해 주시면, 담당 부서에서 적극적으로 검토하여 서비스 개선에 반영할 수 있도록 노력하겠습니다. 더 나은 펫케어를 만드는 데 동참해 주셔서 감사합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '제휴 문의는 어떻게 하나요?', '펫케어와 사업 제휴(예: 미용실/병원 입점, 상품 광고, 공동 프로모션 등)를 원하시는 경우, 홈페이지 하단에 안내된 제휴 문의 이메일 주소 또는 ''고객센터'' > ''1:1 문의하기''를 통해 문의 내용을 남겨주시면 담당자가 검토 후 연락드리겠습니다. 제안하시는 내용과 회사 소개 자료를 함께 보내주시면 더욱 신속한 검토가 가능합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '오류 신고 시 어떤 정보를 함께 전달해야 하나요?', '서비스 이용 중 오류가 발생하여 신고해 주실 때에는, 문제 해결에 도움이 될 수 있도록 가능한 상세한 정보를 함께 전달해 주시면 좋습니다. 예를 들어, 오류 발생 시간, 사용 중이던 메뉴나 기능, 오류 메시지 내용, 문제 상황을 재현할 수 있는 순서, 그리고 가능하다면 오류 화면 스크린샷 등을 첨부해 주시면 원인 파악 및 해결에 큰 도움이 됩니다.', 'admin');

-- 펫 건강 및 관리
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 예방접종은 언제가 적절한가요?', '반려동물의 예방접종은 전염병으로부터 보호하는 매우 중요한 수단입니다. 강아지의 경우 생후 6-8주부터 시작하여 여러 차례 종합백신(DHPPL), 코로나 장염, 켄넬코프, 광견병 등의 접종을 권장합니다. 고양이의 경우 생후 8-9주부터 종합백신(FVRCP), 백혈병, 광견병 등의 접종이 필요할 수 있습니다. 정확한 접종 시기와 종류는 반려동물의 건강 상태, 생활 환경, 지역적 유행병 등을 고려하여 수의사와 상담 후 결정하는 것이 가장 좋습니다. 펫케어 ''반려동물 정보'' 메뉴나 다이어리 기능을 통해 접종 시기를 관리할 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 중성화 수술은 꼭 해야 하나요? 장단점은 무엇인가요?', '중성화 수술은 발정 스트레스 감소, 생식기 질환 예방(자궁축농증, 유선종양, 전립선 질환 등), 문제 행동(마운팅, 스프레이 등) 감소, 원치 않는 임신 방지 등의 장점이 있습니다. 반면, 수술에 따른 마취 위험, 비만 가능성 증가 등의 단점도 고려해야 합니다. 수술 결정은 반려동물의 건강 상태, 품종, 나이, 생활 환경 등을 종합적으로 고려하여 수의사와 충분히 상담 후 신중하게 내리는 것이 중요합니다. 펫케어 커뮤니티에서 다른 반려인들의 경험을 참고하는 것도 도움이 될 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물에게 흔한 질병과 증상은 무엇인가요?', '반려동물에게 흔한 질병으로는 피부병(알레르기, 세균/곰팡이 감염), 소화기 질환(구토, 설사), 치과 질환(치석, 치주염), 그리고 슬개골 탈구(소형견) 등이 있습니다. 초기 증상으로는 식욕 부진, 활력 저하, 특정 부위 핥기, 기침, 재채기, 배변 이상 등이 나타날 수 있습니다. 평소 반려동물의 행동과 상태를 주의 깊게 관찰하고, 이상 증상이 보이면 즉시 동물병원을 방문하여 정확한 진단과 치료를 받는 것이 중요합니다. 펫케어 ''반려동물 정보''에서 질병 관련 정보를 찾아보실 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 구충은 얼마나 자주 해야 하나요?', '반려동물의 내외부 구충은 건강 관리에 매우 중요합니다. 내부 기생충(회충, 촌충 등) 예방을 위해 보통 한 달에 한 번 또는 수의사의 지시에 따라 정기적으로 구충제를 투여합니다. 외부 기생충(벼룩, 진드기) 예방 역시 한 달에 한 번 정도 외부 구충제를 발라주거나 복용하는 것이 일반적입니다. 특히 산책을 자주 하는 경우 더욱 철저한 관리가 필요합니다. 사용하는 구충제의 종류와 주기는 반려동물의 생활 환경과 건강 상태에 따라 달라질 수 있으므로 수의사와 상담하여 결정하세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '노령견/노령묘 관리는 어떻게 해야 하나요?', '7세 이상의 반려동물은 일반적으로 노령기로 간주되며, 이 시기에는 특별한 관리가 필요합니다. 정기적인 건강검진(최소 연 1회)을 통해 노령성 질환(관절염, 심장병, 신부전, 종양 등)을 조기에 발견하고 관리해야 합니다. 소화가 잘 되는 시니어 전용 사료를 급여하고, 관절에 무리가 가지 않는 가벼운 운동을 꾸준히 시켜주는 것이 좋습니다. 또한, 치아 관리, 체온 유지, 편안한 잠자리 제공 등 세심한 보살핌이 필요합니다. 펫케어 ''반려동물 정보''에서 노령 동물 케어 팁을 확인해보세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 응급 상황 시 대처 요령을 알려주세요.', '반려동물에게 응급 상황(교통사고, 추락, 중독, 심한 출혈, 호흡 곤란, 경련 등)이 발생하면 당황하지 말고 침착하게 대처하는 것이 중요합니다. 먼저 반려동물의 안전을 확보하고, 즉시 가장 가까운 24시간 응급 동물병원에 연락하여 상황을 알리고 방문 가능 여부를 확인하세요. 이동 시에는 반려동물이 더 다치지 않도록 조심스럽게 이동시키고, 출혈이 있다면 깨끗한 거즈나 천으로 압박 지혈을 시도할 수 있습니다. 독극물 섭취가 의심될 경우, 섭취한 물질의 종류와 양을 파악하여 병원에 알리는 것이 중요합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 체중 관리는 어떻게 하는 것이 좋을까요?', '반려동물의 비만은 관절 질환, 당뇨병, 심혈관 질환 등 다양한 건강 문제의 원인이 될 수 있으므로 적절한 체중 관리가 매우 중요합니다. 먼저 수의사와 상담하여 반려동물의 이상적인 체중을 파악하고, 사료 급여량을 정확히 계량하여 규칙적으로 제공하세요. 간식은 하루 총 칼로리 섭취량의 10%를 넘지 않도록 제한하는 것이 좋습니다. 또한, 매일 규칙적인 산책이나 놀이 시간을 통해 활동량을 늘려주는 것이 비만 예방 및 관리에 도움이 됩니다. 펫케어 다이어리 기능을 활용하여 체중 변화를 기록하고 관리해보세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 치아 관리는 어떻게 해야 하나요?', '반려동물의 치아 건강은 전반적인 건강 상태와 밀접하게 관련되어 있습니다. 매일 또는 최소 주 2-3회 반려동물 전용 칫솔과 치약을 사용하여 양치질을 해주는 것이 가장 좋습니다. 어릴 때부터 양치질에 익숙해지도록 훈련하는 것이 중요합니다. 양치질이 어렵다면 치석 제거에 도움이 되는 덴탈껌, 덴탈토이, 또는 수의사가 추천하는 구강 관리 제품을 보조적으로 사용할 수 있습니다. 정기적인 동물병원 방문을 통해 구강 검진을 받고, 필요한 경우 스케일링 치료를 받는 것이 좋습니다.', 'admin');

-- 펫 푸드 및 영양
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '어떤 사료를 선택해야 할지 모르겠어요.', '사료 선택은 반려동물의 나이(퍼피/키튼, 어덜트, 시니어), 크기(소형, 중형, 대형), 활동량, 건강 상태(알러지, 특정 질환 유무 등), 그리고 기호성을 종합적으로 고려해야 합니다. 주원료, 영양 성분(조단백, 조지방 함량 등), 그레인프리 여부, 첨가물 등을 꼼꼼히 확인하는 것이 좋습니다. 처음에는 소량 구매하여 기호성 테스트를 해보고, 변 상태나 피부 반응을 관찰하며 적합한 사료를 찾아가는 과정이 필요합니다. 펫케어의 상품 정보나 전문가 Q&A를 참고하시거나, 수의사와 상담하여 추천받는 것도 좋은 방법입니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물에게 사람이 먹는 음식을 줘도 되나요?', '일반적으로 사람이 먹는 음식은 반려동물에게 적합하지 않습니다. 사람 음식에는 염분, 설탕, 향신료 등이 많이 함유되어 있어 반려동물의 건강에 해로울 수 있습니다. 특히 초콜릿, 양파, 마늘, 포도, 특정 견과류 등은 반려동물에게 매우 위험한 독성을 유발할 수 있으므로 절대 급여해서는 안 됩니다. 반려동물에게는 균형 잡힌 영양을 제공하는 전용 사료와 안전한 간식을 급여하는 것이 가장 좋습니다. 궁금한 음식은 급여 전 반드시 수의사에게 확인하세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '사료를 바꿀 때 주의사항이 있나요?', '사료를 갑자기 바꾸면 반려동물이 소화 불량이나 설사, 구토 등의 증상을 보일 수 있습니다. 따라서 새로운 사료로 변경할 때에는 최소 7~10일 정도의 기간을 두고 점진적으로 바꿔주는 것이 좋습니다. 기존 사료에 새로운 사료를 조금씩 섞어 급여하기 시작하여, 매일 새로운 사료의 비율을 점차 늘리고 기존 사료의 비율을 줄여나가면 됩니다. 사료 변경 중에는 반려동물의 변 상태나 식욕, 활동성 등을 주의 깊게 관찰해야 합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물에게 필요한 영양제는 어떤 것이 있나요?', '건강한 반려동물이라도 연령이나 특정 상태에 따라 영양제 급여가 도움이 될 수 있습니다. 예를 들어, 성장기에는 칼슘과 인, 노령기에는 관절 영양제(글루코사민, 콘드로이친)나 항산화제, 피부나 모질 개선을 위해서는 오메가-3 지방산 등이 고려될 수 있습니다. 하지만 모든 반려동물에게 영양제가 필수적인 것은 아니며, 과다 복용은 오히려 건강에 해로울 수 있습니다. 반드시 수의사와 상담하여 반려동물에게 필요한 영양제와 적절한 용량을 추천받아 급여하는 것이 중요합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '자율배식과 제한배식 중 어떤 것이 더 좋은가요?', '자율배식은 사료를 항상 그릇에 채워두어 반려동물이 원할 때마다 먹도록 하는 방식이고, 제한배식은 정해진 시간에 정해진 양만큼 급여하는 방식입니다. 자율배식은 편리할 수 있지만, 과식을 유도하여 비만의 원인이 될 수 있습니다. 특히 식탐이 많은 반려동물에게는 적합하지 않습니다. 제한배식은 정확한 양을 조절할 수 있어 체중 관리에 유리하고, 식사 시간을 통해 교감할 수 있다는 장점이 있습니다. 대부분의 경우, 반려동물의 건강과 행동 조절을 위해 제한배식을 권장합니다. 반려동물의 특성에 맞게 수의사와 상담 후 결정하는 것이 좋습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '수제 간식을 만들 때 주의할 점은 무엇인가요?', '반려동물을 위해 직접 수제 간식을 만들 때에는 몇 가지 주의사항이 있습니다. 첫째, 반려동물에게 안전한 재료만을 사용해야 합니다 (사람에게는 안전하지만 동물에게는 독이 되는 재료 확인 필수). 둘째, 염분, 설탕, 인공 첨가물 사용을 최소화해야 합니다. 셋째, 재료를 충분히 익혀 세균 감염 위험을 줄여야 합니다. 넷째, 알레르기 반응을 일으킬 수 있는 재료는 피하고, 처음 주는 간식은 소량만 급여하여 반응을 살펴보아야 합니다. 만든 간식은 냉장 또는 냉동 보관하고 가급적 빨리 소비하는 것이 좋습니다.', 'admin');

-- 펫 행동 및 훈련
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '강아지 배변 훈련은 어떻게 시키나요?', '강아지 배변 훈련은 인내심과 일관성이 중요합니다. 먼저 배변 패드나 지정된 장소를 마련하고, 강아지가 잠에서 깼을 때, 식사 후, 놀이 후에 해당 장소로 데려가 배변을 유도합니다. 성공적으로 배변했을 때는 즉시 칭찬과 간식으로 보상해 줍니다. 실수했을 경우에는 혼내기보다는 조용히 치우고, 냄새가 남지 않도록 전용 탈취제를 사용하는 것이 좋습니다. 보통 생후 2~4개월 사이에 시작하며, 꾸준한 반복을 통해 학습하게 됩니다. 펫케어 커뮤니티에서 다른 반려인들의 훈련 팁을 공유받을 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '짖음 문제, 어떻게 해결해야 할까요?', '강아지의 짖음에는 다양한 원인(요구성, 경계성, 분리불안 등)이 있을 수 있습니다. 먼저 왜 짖는지 원인을 파악하는 것이 중요합니다. 원인에 따라 대처 방법이 달라집니다. 예를 들어, 요구성 짖음은 무시하고 조용해졌을 때 관심을 주는 것이 효과적일 수 있고, 경계성 짖음은 외부 자극에 둔감해지도록 사회화 훈련을 하거나 환경을 조절해주는 것이 필요합니다. 혼자 있을 때 짖는 분리불안의 경우 점진적인 분리 훈련이 필요합니다. 문제가 심각하다면 동물행동 전문가나 훈련사의 도움을 받는 것이 좋습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 사회화 시기는 언제가 좋고, 어떻게 해야 하나요?', '반려동물의 사회화는 생후 3주에서 3~4개월 사이가 가장 중요한 시기입니다. 이 시기에 다양한 사람, 다른 동물, 여러 가지 소리 및 환경에 긍정적인 경험을 쌓게 해주는 것이 중요합니다. 안전하고 통제된 환경에서 다른 온순한 강아지나 사람들과 만나게 하고, 자동차 소리, 초인종 소리 등 생활 소음에 익숙해지도록 도와주세요. 긍정적인 경험을 통해 사회성을 기르면 성견이 되어서도 문제 행동을 줄이고 안정적인 성격을 갖는 데 도움이 됩니다. 너무 많은 자극을 한꺼번에 주는 것은 피해야 합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '고양이가 스크래치를 아무데나 해요. 어떻게 하죠?', '고양이에게 스크래칭은 발톱 관리, 영역 표시, 스트레스 해소 등 자연스러운 행동입니다. 이를 막을 수는 없으므로, 허용된 장소에 스크래칭을 하도록 유도하는 것이 중요합니다. 다양한 재질(삼줄, 카펫, 골판지 등)과 형태(수직형, 수평형)의 스크래처를 여러 곳에 마련해주고, 고양이가 선호하는 것을 찾도록 도와주세요. 스크래처에 캣닢을 뿌리거나 장난감으로 유도하는 것도 좋은 방법입니다. 원치 않는 곳에 스크래치를 한다면, 해당 장소에 양면테이프를 붙이거나 고양이가 싫어하는 향(감귤류 등)을 뿌려둘 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '산책 시 다른 강아지나 사람에게 공격성을 보여요.', '산책 중 공격성은 두려움, 영역 방어, 과거의 나쁜 경험 등 다양한 원인으로 발생할 수 있습니다. 먼저 안전을 위해 입마개 착용을 고려하고, 다른 강아지나 사람과 충분한 거리를 유지하며 산책하세요. 자극이 되는 대상이 나타나면 간식이나 장난감으로 주의를 돌리고, 침착하게 지나치면 칭찬과 보상을 해주는 긍정 강화 훈련이 도움이 될 수 있습니다. 전문가(수의사 또는 행동교정 전문가)와 상담하여 정확한 원인을 진단받고 체계적인 행동 교정 프로그램을 진행하는 것이 가장 효과적입니다.', 'admin');

-- 펫 용품 및 환경
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물에게 안전한 장난감은 어떤 것인가요?', '반려동물에게 안전한 장난감은 삼킬 수 없는 크기여야 하며, 쉽게 부서지거나 날카로운 조각이 나오지 않는 내구성이 좋은 제품이어야 합니다. 천연 고무, 실리콘, 단단한 나일론 소재 등이 비교적 안전합니다. 작은 부품이 달린 장난감이나 쉽게 뜯어먹을 수 있는 스펀지류, 화학 처리가 된 가죽 제품 등은 피하는 것이 좋습니다. 항상 반려동물이 장난감을 가지고 노는 모습을 지켜보고, 파손된 장난감은 즉시 교체해 주어야 합니다. 반려동물의 크기와 성향에 맞는 장난감을 선택해 주세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물을 위한 실내 환경은 어떻게 조성해야 하나요?', '반려동물이 안전하고 편안하게 지낼 수 있는 실내 환경 조성이 중요합니다. 위험한 물건(전선, 약품, 작은 물체 등)은 반려동물의 발이 닿지 않는 곳에 보관하고, 미끄러운 바닥에는 미끄럼 방지 매트를 깔아주는 것이 좋습니다. 편안하게 쉴 수 있는 자신만의 공간(하우스, 쿠션)을 마련해주고, 적절한 온도와 습도를 유지해야 합니다. 고양이의 경우 수직 공간(캣타워 등)을 제공하면 스트레스 해소에 도움이 됩니다. 환기를 자주 시켜 쾌적한 공기를 유지하는 것도 중요합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '여름철/겨울철 반려동물 관리는 어떻게 다른가요?', '여름철에는 더위로 인한 열사병을 주의해야 합니다. 한낮의 뜨거운 시간대에는 산책을 피하고, 물을 충분히 마실 수 있도록 해주며, 시원한 환경을 유지해야 합니다. 차 안에 반려동물을 혼자 두는 것은 매우 위험합니다. 겨울철에는 추위로 인한 저체온증이나 동상을 예방해야 합니다. 실내 온도를 따뜻하게 유지하고, 산책 시에는 필요에 따라 옷을 입히고 발바닥 보호에도 신경 써야 합니다. 난방기구 사용 시 화상이나 건조함에 주의하고, 충분한 수분 섭취를 도와주세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물과 함께 여행 시 준비물은 무엇인가요?', '반려동물과 함께 여행을 떠날 때에는 평소 사용하던 사료와 물, 식기, 배변봉투/패드, 목줄/하네스, 이동장(켄넬), 그리고 반려동물의 신분증(인식표, 등록칩 정보)을 반드시 챙겨야 합니다. 여행지의 기후나 환경에 따라 추가로 필요한 물품(예: 해충 기피제, 여벌의 수건, 상비약 등)도 준비하는 것이 좋습니다. 장거리 이동 시에는 멀미 예방을 위해 출발 전 수의사와 상담하여 멀미약을 처방받을 수도 있습니다. 숙소나 방문 장소가 반려동물 동반이 가능한지 사전에 확인하는 것은 필수입니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '새로운 반려동물을 입양했을 때 기존 반려동물과의 합사는 어떻게 하나요?', '새로운 반려동물과 기존 반려동물의 합사는 신중하고 점진적으로 진행해야 합니다. 처음에는 서로의 존재를 냄새로만 인지하도록 분리된 공간에서 생활하게 하고, 점차적으로 문틈으로 서로를 보게 하거나, 짧은 시간 동안 감독 하에 만나게 합니다. 긍정적인 경험을 쌓을 수 있도록 만남 동안에는 간식을 주거나 칭찬을 해주는 것이 좋습니다. 각자의 식기, 잠자리, 장난감 등을 따로 마련해주어 경쟁을 줄여야 합니다. 서두르지 않고 각 동물의 성향에 맞춰 천천히 진행하는 것이 중요하며, 필요하다면 전문가의 도움을 받는 것도 고려해 보세요.', 'admin');

-- 법률 및 정책
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 등록제는 무엇이며 어떻게 해야 하나요?', '반려동물 등록제는 동물 보호와 유실·유기 방지를 위해 시행되는 제도로, 주택·준주택에서 기르거나 그 외의 장소에서 반려 목적으로 기르는 2개월령 이상의 개는 의무적으로 지방자치단체에 등록해야 합니다. 등록은 시·군·구청이나 동물등록 대행기관(동물병원 등)을 통해 가능하며, 내장형 무선식별장치(마이크로칩) 또는 외장형 무선식별장치 중 선택하여 등록할 수 있습니다. 등록 시 소유자 정보, 반려동물 정보 등이 기록되며, 미등록 시 과태료가 부과될 수 있습니다. 고양이는 현재 의무는 아니나 일부 지자체에서 시범적으로 시행 중입니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '산책 시 목줄 착용과 배변 처리는 필수인가요?', '네, 동물보호법에 따라 반려견과 외출 시에는 반드시 목줄, 가슴줄 등 안전조치를 해야 하며, 다른 사람에게 위해를 주거나 혐오감을 주지 않도록 관리해야 합니다. 특히 맹견으로 지정된 품종의 경우 입마개 착용도 의무입니다. 또한, 반려인이 배설물(소변의 경우 공동생활 공간의 건물 내부 공용공간 및 엘리베이터 등 한정)을 즉시 수거하지 않을 경우 과태료가 부과됩니다. 이는 타인에 대한 배려이자 성숙한 반려 문화의 기본입니다. 펫티켓을 지켜주세요.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '맹견의 종류와 관리 의무는 무엇인가요?', '현행 동물보호법상 맹견으로 지정된 품종은 도사견, 아메리칸 핏불테리어, 아메리칸 스태퍼드셔 테리어, 스태퍼드셔 불 테리어, 로트와일러와 그 잡종의 개입니다. 맹견 소유자는 안전한 사육 및 관리를 위해 정기적인 교육 이수, 외출 시 목줄과 입마개 필수 착용, 어린이집·유치원·초등학교 등 특정 장소 출입 금지 등의 의무를 준수해야 합니다. 또한, 맹견으로 인한 다른 사람의 생명·신체·재산상 피해를 보상하기 위해 보험 가입도 의무화되어 있습니다. 자세한 내용은 동물보호관리시스템에서 확인 가능합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '유기동물을 발견했을 때 어떻게 해야 하나요?', '길에서 주인을 잃거나 유기된 것으로 보이는 동물을 발견했을 경우, 함부로 만지거나 다가가기보다는 먼저 해당 지역의 시·군·구청 동물보호 담당 부서나 유기동물 구조 단체에 신고하는 것이 안전합니다. 동물이 공격성을 보이거나 다친 상태일 수 있으므로 주의해야 합니다. 가능하다면 동물의 사진을 찍어두고 발견 장소, 시간, 동물의 상태 등을 자세히 알려주시면 구조에 도움이 됩니다. 직접 구조하여 임시 보호를 하게 될 경우에도 반드시 관할 지자체에 신고하여 주인을 찾거나 정식 입양 절차를 밟을 수 있도록 해야 합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 관련 분쟁 발생 시 도움을 받을 수 있는 곳이 있나요?', '반려동물로 인해 이웃 간 분쟁(소음, 배설물 등)이 발생하거나, 동물병원 의료 과실, 펫샵에서의 부당한 계약 등으로 피해를 입었을 경우, 먼저 당사자 간 원만한 합의를 시도하는 것이 좋습니다. 합의가 어렵다면 대한법률구조공단, 한국소비자원, 또는 각 지자체의 관련 부서(환경과, 동물보호과 등)에 상담을 요청하여 중재나 법적 도움을 받을 수 있는지 알아볼 수 있습니다. 동물 관련 전문 변호사의 도움을 받는 것도 하나의 방법입니다. 펫케어 커뮤니티에서도 유사 경험자들의 조언을 얻을 수 있습니다.', 'admin');

-- 기술 지원 및 기타
INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '앱 업데이트는 어떻게 하나요? 자동 업데이트가 되나요?', '펫케어 모바일 앱은 더 나은 서비스와 새로운 기능을 제공하기 위해 주기적으로 업데이트됩니다. 사용하시는 스마트폰의 앱 스토어(구글 플레이 스토어, 애플 앱 스토어) 설정에서 앱 자동 업데이트 기능을 활성화해두시면, Wi-Fi 환경에서 새로운 버전이 출시될 때 자동으로 업데이트됩니다. 수동으로 업데이트하시려면 각 앱 스토어에서 ''펫케어''를 검색하여 ''업데이트'' 버튼을 누르시면 됩니다. 최신 버전으로 업데이트하시면 더욱 안정적이고 개선된 서비스를 이용하실 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '서비스 이용 중 데이터는 안전하게 보호되나요?', '펫케어는 회원님의 개인정보와 서비스 이용 데이터를 매우 중요하게 생각하며, 정보통신망법 등 관련 법규를 준수하여 안전하게 관리하고 있습니다. 수집된 정보는 암호화되어 저장되며, 정기적인 보안 점검과 시스템 업데이트를 통해 해킹이나 정보 유출로부터 보호하기 위해 최선을 다하고 있습니다. 자세한 내용은 홈페이지 하단의 ''개인정보 처리 방침''을 참고해 주시기 바랍니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '지원되는 브라우저나 운영체제(OS) 버전이 있나요?', '펫케어 웹 서비스는 최신 버전의 주요 웹 브라우저(크롬, 엣지, 사파리, 파이어폭스 등)에서 원활하게 이용하실 수 있도록 개발되었습니다. 인터넷 익스플로러(IE)와 같이 지원이 중단된 브라우저에서는 일부 기능이 정상적으로 작동하지 않을 수 있습니다. 모바일 앱의 경우, 안드로이드 및 iOS의 특정 버전 이상을 지원하며, 지원 OS 버전은 앱 스토어의 앱 정보 페이지에서 확인하실 수 있습니다. 최적의 서비스 이용을 위해 가급적 최신 환경으로 업데이트하여 사용해 주시기를 권장합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '해외에서도 펫케어 서비스를 이용할 수 있나요?', '펫케어 서비스는 기본적으로 대한민국 내 사용자들을 대상으로 제공되고 있습니다. 일부 정보 조회나 커뮤니티 활동 등은 해외에서도 인터넷 접속이 가능하다면 이용하실 수 있으나, 미용/병원 예약이나 상품 구매와 같은 지역 기반 서비스는 국내로 한정될 수 있습니다. 또한, 해외에서의 접속 환경에 따라 서비스 속도나 안정성에 차이가 있을 수 있습니다. 해외에서의 서비스 이용과 관련된 구체적인 제약 사항은 추후 공지를 통해 안내드리겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '펫케어 서비스의 향후 발전 계획이 궁금합니다.', '펫케어는 현재 제공 중인 미용/병원 예약, 정보 서비스, 커뮤니티, 입양/분양 등의 핵심 기능을 지속적으로 고도화하는 한편, 사용자분들의 요구를 반영한 새로운 기능들을 추가해나갈 계획입니다. 예를 들어, AI 기반 맞춤형 상품/서비스 추천, 반려동물 건강 관리 심층 분석 도구, 위치 기반 산책 친구 찾기, 온라인 펫 박람회 개최 등 다양한 아이디어를 검토하고 있습니다. 사용자 여러분의 목소리에 귀 기울이며 반려동물과 반려인이 더욱 행복해질 수 있는 최고의 플랫폼으로 성장해 나가겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '비밀번호 찾기 시 이메일이 오지 않아요.', '비밀번호 찾기를 시도했음에도 불구하고 인증 이메일이 수신되지 않는 경우, 몇 가지 사항을 확인해 주시기 바랍니다. 첫째, 가입 시 등록한 이메일 주소가 정확한지 다시 한번 확인해 주세요. 둘째, 스팸 메일함이나 프로모션 메일함으로 분류되지 않았는지 확인해 주세요. 셋째, 사용하시는 이메일 서비스 제공업체의 설정에서 펫케어(@petcare.com 등)에서 발송하는 메일이 차단되어 있지 않은지 확인해 주세요. 위 사항들을 모두 확인했음에도 문제가 해결되지 않으면, 고객센터 1:1 문의를 통해 문의해 주시면 계정 확인 후 도움을 드리겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '푸시 알림이 오지 않는데 어떻게 설정해야 하나요?', '펫케어 앱에서 푸시 알림이 오지 않는 경우, 다음 두 가지 설정을 확인해 보시기 바랍니다. 첫째, 펫케어 앱 내의 ''마이페이지 > 알림 설정''에서 원하시는 항목의 알림이 켜져 있는지 확인해 주세요. 둘째, 사용하시는 스마트폰 자체의 알림 설정에서 펫케어 앱의 알림 권한이 허용되어 있는지 확인해야 합니다. (안드로이드: 설정 > 애플리케이션 > 펫케어 > 알림 / iOS: 설정 > 알림 > 펫케어). 두 가지 설정이 모두 올바르게 되어 있음에도 알림이 오지 않는다면, 앱을 완전히 종료 후 재시작하거나, 앱을 삭제 후 재설치해 보시고, 그래도 문제가 지속되면 고객센터로 문의해 주십시오.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '예약 시 결제는 어떻게 이루어지나요? 선결제인가요?', '펫케어를 통한 미용실 또는 동물병원 예약 시 결제 방식은 각 업체 정책에 따라 다를 수 있습니다. 일부 업체의 경우, 예약 확정을 위해 서비스 금액의 일부 또는 전체를 선결제(온라인 결제)하도록 요구할 수 있습니다. 다른 업체의 경우에는 예약만 진행하고 실제 서비스 이용 후 현장에서 결제하는 방식일 수 있습니다. 예약 진행 과정에서 결제 관련 안내가 명확히 표시되오니, 해당 내용을 꼼꼼히 확인하시고 진행해 주시기 바랍니다. 결제 관련 문의사항은 해당 업체에 직접 문의하시는 것이 가장 정확합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '커뮤니티에서 특정 키워드로 게시글을 검색할 수 있나요?', '네, 펫케어 커뮤니티 내에서는 특정 키워드를 사용하여 원하시는 정보가 담긴 게시글을 쉽게 찾아보실 수 있습니다. 각 게시판 상단 또는 커뮤니티 메인 페이지에 위치한 검색창에 찾고 싶은 단어(예: "강아지 사료 추천", "고양이 장난감 후기")를 입력하고 검색 버튼을 누르시면, 제목이나 내용에 해당 키워드가 포함된 게시글 목록을 보여줍니다. 검색 옵션을 통해 작성자나 기간별로도 필터링할 수 있는 기능을 제공하고 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '입양/분양 게시판에 반려동물 용품 나눔 글도 올려도 되나요?', '펫케어의 입양/분양 게시판은 기본적으로 새로운 가족을 찾는 반려동물들을 위한 공간입니다. 반려동물 용품 나눔이나 중고 거래를 위한 글은 해당 게시판의 취지와는 다소 맞지 않을 수 있습니다. 용품 나눔이나 거래를 원하시면, 커뮤니티 내 ''자유 게시판'' 또는 별도로 마련될 수 있는 ''나눔/거래 게시판(가칭)''을 이용해 주시기를 권장합니다. 명확한 카테고리 구분을 통해 사용자들이 원하는 정보를 더 쉽게 찾을 수 있도록 하기 위함이니 양해 부탁드립니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '다이어리에 기록할 수 있는 반려동물의 건강 정보 항목은 무엇인가요?', '펫케어의 반려동물 다이어리 기능에는 반려동물의 건강 관리를 위한 다양한 항목을 기록할 수 있습니다. 기본적으로 체중 변화, 예방접종 내역(접종일, 백신 종류, 다음 접종 예정일), 구충 기록, 병원 방문 기록(진료 내용, 처방약, 진료비 등), 특정 증상(구토, 설사, 기침 등) 발생일 및 내용, 그리고 알레르기 정보 등을 상세하게 기록하고 관리할 수 있습니다. 이를 통해 반려동물의 건강 상태를 꾸준히 모니터링하고, 병원 방문 시 수의사에게 정확한 정보를 전달하는 데 도움이 됩니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '상품 정보에서 본 상품을 바로 구매할 수 있나요?', '펫케어의 상품 정보 서비스는 다양한 반려동물 관련 상품들의 정보를 비교하고 탐색할 수 있도록 제공하는 것을 주 목적으로 합니다. 대부분의 경우, 상품 상세 정보 페이지에서 해당 상품을 판매하는 외부 제휴 쇼핑몰로 이동할 수 있는 링크를 제공하고 있습니다. 따라서 실제 구매는 해당 외부 쇼핑몰의 결제 시스템을 통해 이루어지게 됩니다. 펫케어 플랫폼 내에서 직접 결제 및 배송 처리까지 이루어지는 자체 쇼핑몰 기능은 현재 준비 중에 있으며, 향후 서비스될 예정입니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '전문가 Q&A에서 답변해주는 전문가는 어떤 분들인가요?', '펫케어의 전문가 Q&A 서비스에 답변을 제공하는 전문가들은 저희와 제휴를 맺은 현직 수의사, 반려동물 행동교정 전문가(훈련사), 펫 영양학 전문가, 또는 반려동물 미용 전문가 등 각 분야에서 다년간의 경험과 전문 지식을 갖춘 분들입니다. 질문의 성격에 따라 해당 분야의 전문가가 답변을 드리거나, 경우에 따라서는 펫케어 운영팀에서 일반적인 정보를 제공해 드릴 수도 있습니다. 저희는 신뢰할 수 있는 정보를 제공하기 위해 전문가 섭외 및 검증에 노력을 기울이고 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '1:1 문의 시 첨부파일은 어떤 형식으로 올릴 수 있나요?', '고객센터 1:1 문의 시 문제 상황 설명이나 자료 첨부를 위해 파일을 올리실 경우, 일반적으로 사용되는 이미지 파일 형식(JPG, PNG, GIF)이나 문서 파일 형식(PDF, DOC, HWP, TXT) 등을 지원합니다. 파일 1개당 업로드 용량 제한이 있을 수 있으며(예: 5MB 이내), 여러 파일을 첨부해야 할 경우 압축파일(ZIP) 형태로 묶어서 올려주시면 편리합니다. 동영상 파일이나 실행 파일(.exe) 등 일부 형식은 보안상의 이유로 첨부가 제한될 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '회원 등급이나 활동 포인트 같은 제도가 있나요?', '현재 펫케어 서비스에는 공식적인 회원 등급 제도나 활동에 따른 포인트 적립/사용 제도는 운영되고 있지 않습니다. 다만, 커뮤니티 활동 우수 회원 선정이나 특정 이벤트 참여 시 리워드를 제공하는 등의 비정기적인 혜택은 있을 수 있습니다. 향후 사용자분들의 서비스 이용 활성화를 위해 회원 등급제, 포인트 제도, 또는 다양한 참여형 보상 프로그램 도입을 긍정적으로 검토하고 있습니다. 좋은 의견이 있으시면 언제든지 제안해 주시기 바랍니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '탈퇴한 아이디로 재가입이 가능한가요?', '회원 탈퇴 후 동일한 아이디(이메일 주소)로의 재가입은 일정 기간 제한될 수 있습니다. 이는 서비스 부정 이용 방지 및 정보 처리와 관련된 내부 정책에 따른 것입니다. 재가입 제한 기간은 통상 30일에서 90일 사이로 설정될 수 있으며, 이 기간이 지나면 동일 아이디로 다시 회원가입을 시도하실 수 있습니다. 만약 급하게 재가입이 필요하시거나 관련하여 궁금한 점이 있으시면 고객센터로 문의해 주시면 확인 후 안내해 드리겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '미용/병원 예약 알림은 언제 오나요? (예: 하루 전, 당일)', '펫케어를 통해 미용실이나 동물병원 예약을 완료하시면, 예약 확정 알림과 함께 예약일이 다가올 때 리마인드 알림을 보내드립니다. 일반적으로 예약일 하루 전, 그리고 예약 당일 몇 시간 전에 푸시 알림이나 SMS를 통해 예약 시간과 장소를 다시 한번 안내해 드리고 있습니다. 알림 수신 설정은 ''마이페이지 > 알림 설정''에서 변경하실 수 있습니다. 중요한 예약을 잊지 않도록 돕기 위한 기능이니 유용하게 활용해 주시기 바랍니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '커뮤니티 글쓰기 시 임시저장 기능이 있나요?', '네, 펫케어 커뮤니티에서 게시글을 작성하시는 동안 예기치 않은 상황(브라우저 종료, 인터넷 연결 끊김 등)으로 인해 작성 중인 내용이 사라지는 것을 방지하기 위해 임시저장 기능을 제공하고 있습니다. 일정 시간 간격으로 자동으로 저장되거나, 사용자가 직접 임시저장 버튼을 클릭하여 저장할 수 있습니다. 나중에 이어서 작성하시려면 ''마이페이지 > 내가 쓴 글 > 임시저장 글'' 목록에서 해당 글을 불러와 계속 작성하실 수 있습니다. 다만, 완벽한 복구를 보장하는 것은 아니므로 중요한 내용은 별도로 백업해두시는 것이 안전합니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '광고성 스팸 게시물을 발견하면 어떻게 해야 하나요?', '펫케어 커뮤니티에서 서비스와 무관한 광고, 홍보, 스팸성 게시물이나 댓글을 발견하시면 즉시 해당 게시물 하단이나 댓글 옆의 ''신고하기'' 버튼을 눌러 신고해 주시기 바랍니다. 신고 시 ''광고/홍보'' 사유를 선택해 주시면 운영팀에서 빠르게 확인하고 조치할 수 있습니다. 깨끗하고 유익한 커뮤니티 환경을 유지하기 위해 사용자 여러분의 적극적인 협조를 부탁드립니다. 반복적으로 스팸 게시물을 올리는 사용자는 계정 이용이 제한될 수 있습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '이벤트나 프로모션 정보는 어디서 확인할 수 있나요?', '펫케어에서 진행하는 다양한 이벤트, 할인 프로모션, 제휴 혜택 등의 정보는 주로 홈페이지 내 ''이벤트'' 메뉴 또는 ''공지사항'' 게시판을 통해 안내해 드리고 있습니다. 또한, 앱 푸시 알림이나 이메일 뉴스레터(수신 동의 시)를 통해서도 새로운 소식을 받아보실 수 있습니다. 정기적으로 방문하셔서 유용한 혜택을 놓치지 마세요. 이벤트 참여 조건이나 기간 등을 꼼꼼히 확인하시고 참여해 주시기 바랍니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '펫케어 서비스 이용 약관은 어디서 볼 수 있나요?', '펫케어 서비스 이용 약관은 회원가입 시 동의 절차를 거치게 되며, 가입 이후에도 언제든지 확인하실 수 있도록 홈페이지 가장 하단에 ''이용 약관'' 링크를 제공하고 있습니다. 이용 약관에는 서비스의 제공 및 이용 조건, 회원의 권리와 의무, 책임 제한, 분쟁 해결 등 서비스 이용에 관한 전반적인 내용이 명시되어 있으니, 서비스 이용 전에 한 번쯤 읽어보시는 것이 좋습니다. 약관이 변경될 경우에는 시행일 최소 7일 전에 공지사항을 통해 알려드립니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '반려동물 관련 보험 상품 정보도 제공하나요?', '현재 펫케어는 반려동물 보험(펫보험) 상품을 직접 중개하거나 판매하고 있지는 않습니다. 다만, 반려동물 양육에 필요한 유용한 정보의 일환으로 ''반려동물 정보'' 섹션이나 전문가 Q&A, 커뮤니티 등을 통해 펫보험의 필요성, 종류, 가입 시 고려사항 등에 대한 일반적인 정보를 제공할 수는 있습니다. 향후 신뢰할 수 있는 보험사와의 제휴를 통해 관련 정보를 제공하거나 비교 서비스를 도입하는 방안을 검토하고 있습니다. 보험 가입은 각 보험사의 상품 내용을 충분히 검토 후 결정하시기 바랍니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '내 주변 동물 약국 정보도 알 수 있나요?', '펫케어 서비스는 현재 미용실 및 동물병원 정보 제공에 중점을 두고 있습니다. 동물 약국에 대한 정보는 아직 직접적으로 제공하고 있지 않지만, 많은 동물병원에서 기본적인 약품 처방 및 판매를 겸하고 있으므로, ''병원 서비스''에서 검색된 동물병원에 문의하시면 필요한 약품 구매 가능 여부를 확인하실 수 있습니다. 향후 사용자 편의를 위해 동물 약국 정보까지 포함하여 제공 범위를 확대하는 방안도 고려하도록 하겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '데이터 백업은 얼마나 자주 이루어지나요?', '펫케어는 회원님들의 소중한 데이터(회원 정보, 게시글, 다이어리 내용 등)를 보호하기 위해 정기적으로 시스템 백업을 실시하고 있습니다. 백업 주기는 데이터의 중요도와 서비스 안정성에 따라 다르지만, 일반적으로 매일 또는 주 단위로 전체 및 증분 백업을 진행하고 있습니다. 또한, 물리적 서버 장애나 자연재해에 대비하여 이중화된 시스템 및 원격지 백업 등의 조치를 통해 데이터 유실 위험을 최소화하기 위해 노력하고 있습니다. 안심하고 서비스를 이용하실 수 있도록 최선을 다하겠습니다.', 'admin');

INSERT INTO FAQ (faq_no, faq_title, faq_content, faq_author)
VALUES (SEQ_FAQ_NO.NEXTVAL, '고객센터 운영 시간은 어떻게 되나요?', '펫케어 고객센터의 1:1 문의 게시판은 24시간 언제든지 이용 가능하며, 문의하신 내용에 대한 답변은 평일 오전 10시부터 오후 6시까지(주말 및 공휴일 제외) 순차적으로 처리해 드리고 있습니다. 운영 시간 외에 접수된 문의는 다음 영업일에 확인 후 답변드립니다. 긴급한 사안이나 시스템 장애 발생 시에는 공지사항을 통해 별도의 안내를 드릴 수 있습니다. 문의량이 많을 경우 답변이 다소 지연될 수 있는 점 양해 부탁드립니다.', 'admin');

COMMIT;

SET DEFINE ON;
